mapper.select:
select

from ymt_orders
where order_refer = #{orderRefer} and order_status = #{status}
order by createtime desc
LIMIT 10 offset ${(page-1)*10};

select

from ymt_orders
where order_refer = #{openid}
group by cus_phone
order by createtime desc
LIMIT 10 offset ${(page-1)*10}

select

from ymt_orders
where cus_phone = #{cusPhone}
order by createtime desc
LIMIT 10 offset ${(page-1)*10}

select

from ymt_orders
where order_refer = #{openid} and order_status = #{status}

select

from ymt_orders
where order_refer = #{openid} and order_id = #{oid}

select

from ymt_orders
where cus_phone = #{cusPhone}
order by createtime desc
LIMIT 1;

select

from ymt_orders
where order_service_openid = #{openId}
and order_substatus = 1;

mapper.update:
update ymt_orders
set order_substatus = 3
where order_id = #{orderId,jdbcType=VARCHAR}
