mapper.update:
update ymt_user
set  user_nickname = #{userNickname,jdbcType=VARCHAR},
  user_headimgurl = #{userHeadimgurl,jdbcType=VARCHAR}
where user_openid = #{userOpenid}

update ymt_user
set user_status = 2
where id = #{id,jdbcType=INTEGER}

update ymt_user
set user_shortUrl = #{userShorturl}
where id = #{id}
